function filterEmojis(categories) {
  const results = [];
  for (let i = 0; i < categories.length; i++) {
    results[i] = Object.assign({}, categories[i]);
    results[i].emojis = categories[i].emojis.filter( ( el ) => !emojisNotSupportOnMac[i].emojis.includes( el ) );
    results[i].emojis = categories[i].emojis.filter( ( el ) => !emojisNotSupportOnWin[i].emojis.includes( el ) );
  }
  return results;
}

