var TwemojiInput = function(obj){
  /* Declarative Part */

  let Emoji = filterEmojis(categories)
      prefix = 'twemoji',
      size = 32,

      list = document.createElement('div'),
      listWrap = document.createElement('div'),
      popover = document.createElement('div'),
      wrapper = document.createElement('div'),
      box = document.createElement('div'),
      defaultIcon = document.createElement('div'),
      emojiInfo = document.createElement('div'),
      isHover = false;

  const informationOfEmoji = {
    image: null,
    detail: {
      text: null,
      html_code: null
    }
  }

  

  function insert(txtarea, text){
    if (!txtarea) {
      return;
    }

    var scrollPos = txtarea.scrollTop;
    var strPos = 0;
    var br = ((txtarea.selectionStart || txtarea.selectionStart == '0') ?
      "ff" : (document.selection ? "ie" : false));
    if (br === false) {
      return;
    }

    if (br == "ie") {
      txtarea.focus();
      var range = document.selection.createRange();
      range.moveStart('character', -txtarea.value.length);
      strPos = range.text.length;
    } else if (br == "ff") {
      strPos = txtarea.selectionStart;
    }

    var front = (txtarea.value).substring(0, strPos);
    var back = (txtarea.value).substring(strPos, txtarea.value.length);
    txtarea.value = front + text + back;
    strPos = strPos + text.length;
    if (br == "ie") {
      txtarea.focus();
      var ieRange = document.selection.createRange();
      ieRange.moveStart('character', -txtarea.value.length);
      ieRange.moveStart('character', strPos);
      ieRange.moveEnd('character', 0);
      ieRange.select();
    } else if (br == "ff") {
      txtarea.selectionStart = strPos;
      txtarea.selectionEnd = strPos;
      txtarea.focus();
    }

    txtarea.scrollTop = scrollPos;
  };

  function wrap(){
    wrapper.className = prefix + '_wrap';
    box.className = prefix + '_box';
    defaultIcon.className = prefix + '_default';
    popover.className = prefix + '_popover';
    listWrap.className = prefix + '_list_wrap';
    list.className = "list-emoji";
    emojiInfo.className = prefix + '-' + 'info';

    obj.parentNode.insertBefore(wrapper, obj.nextSibling);

    box.appendChild(defaultIcon);
    box.appendChild(popover);
    popover.appendChild(listWrap);
    listWrap.appendChild(list);
    listWrap.appendChild(emojiInfo);

    wrapper.appendChild(box);
  };


  function updateList(listSize){
    var l = Emoji.length,
        w = popover.offsetWidth,
        h = popover.offsetHeight,
        listSize = listSize || 25,
        listLength = list.children.length;

    for (let i = 0; i < l; i++) {
      function createItem(){
        let category = document.createElement('div'),
            category_name = document.createElement('div'),
            category_name_span = document.createElement('span'),
            category_emoji = document.createElement("div"),
            category_emoji_block = document.createElement("div");
        
        category.className = "category";
        category_name.className = "category-name";
        category_name_span.innerHTML = Emoji[i].name;
        category_name.appendChild(category_name_span);
        category.appendChild(category_name);
        category_emoji.className = 'category-emoji';
        category_emoji_block.className = 'category-emoji-block';

        let emoji_length = Emoji[i].emojis.length

        for(let j = 0; j < emoji_length; j++) {
          let emoji_block = document.createElement("div");

          // emoji_block.className = 'emoji-block';

          let parsedTwemoji = twemoji.parse(Emoji[i].emojis[j]);
          emoji_block.innerHTML = parsedTwemoji;
          let emoji_span = createImgSpan(emoji_block.children[0].src, emoji_block.children[0].alt);
          category_emoji_block.appendChild(emoji_span);

          emoji_span.onclick = function(e) {
            let alt = this.children[0].textContent;

            insert(obj, alt);
          }

          emoji_span.onmouseover = function(e) {
            isHover = true;
            const emoji = this.textContent;
            setInformation(
              twemoji.parse(emoji),
              emoji,
              convertEmojiToHtmlCode(emoji)
            );
          }

          if ((j+1) % 9 === 0) {
            category_emoji.appendChild(category_emoji_block);
            category_emoji_block = document.createElement("div");
            category_emoji_block.className = 'category-emoji-block';
          } else if (j === Emoji[i].emojis.length - 1) {
            category_emoji.appendChild(category_emoji_block);
            category_emoji_block = document.createElement("div");
            category_emoji_block.className = 'category-emoji-block';
          }
        }
        category.appendChild(category_emoji);
        list.appendChild(category)
      }
      if(Emoji[i]){
        createItem();
      }
    }
  };


  var customScroll = function(content, options){
    var wrap = document.createElement('div');

    wrap.className = 'scroll-wrap';
    wrap.style.height = options ? options.height+'px' : '320px';

    window.onload=function() {
      content.parentNode.insertBefore(wrap, content);
      wrap.appendChild(content);
    }
  };

  const information = function() {
    const emoji_image = document.createElement('div');
    const emoji_text = document.createElement('div');
    const emoji_text_icon = document.createElement('div');
    const emoji_text_html_code = document.createElement('div');
    const emoji_text_icon_span = document.createElement('span');
    const emoji_text_html_code_span = document.createElement('span');

    informationOfEmoji.image = emoji_image;
    informationOfEmoji.detail.text = emoji_text_icon_span;
    informationOfEmoji.detail.html_code = emoji_text_html_code_span;

    emoji_image.className = 'emoji-image';
    emoji_text.className = 'emoji-detail';
    emoji_text_icon.className = 'emoji-text-icon';
    emoji_text_html_code.className = 'emoji-text-html-code';
    emoji_image.innerHTML = twemoji.parse('ğŸ‘‹');
    emoji_text_icon.appendChild(emoji_text_icon_span);
    emoji_text_html_code.appendChild(emoji_text_html_code_span);
    emoji_text.appendChild(emoji_text_icon);
    emoji_text.appendChild(emoji_text_html_code);

    emojiInfo.appendChild(emoji_image);
    emojiInfo.appendChild(emoji_text);
  }

  function convertEmojiToHtmlCode(emoji) {
    const codePoint = twemoji.convert.toCodePoint(emoji).split('-');
    return codePoint.map(function (codepoint) {
      return '&#x' + codepoint.toUpperCase() + ';';
    }).join('');
  }

  function createImgSpan(src, text) {
    let img_span = document.createElement("span");
    let circle_span = document.createElement("span");
    let offset_span = document.createElement("span");
    let text_span = document.createElement("span");
    img_span.className = 'emoji-text';
    img_span.style.cssText = `background-image: url(${src}); background-size: 1em 1em; padding: 0.15em; background-position: center center; background-repeat: no-repeat;`;
    circle_span.style.cssText = 'clip-path: circle(0% at 50% 50%);';
    text_span.innerText = text;
    offset_span.appendChild(text_span)
    circle_span.appendChild(offset_span)
    img_span.appendChild(circle_span)
    return img_span;
  }

  function setInformation(img, emojiText='', htmlCode='') {
    informationOfEmoji.image.innerHTML = img;
    informationOfEmoji.detail.text.textContent = emojiText;
    informationOfEmoji.detail.html_code.textContent = htmlCode;
  }

  function init(){
    wrap();
    customScroll(list);
    updateList();
    information();
  };

  /* Application Part */

  function toggleBox(){
    var currentClass = wrapper.className;
    if(currentClass.match('__open','g')){
      wrapper.className = currentClass.replace(/ __open/g,'')
    } else {
       wrapper.className += ' __open';
    }
  }

  defaultIcon.onclick = function(e){
    e.preventDefault();
    toggleBox();
  };

  document.onclick = function(e){
    if (e.target == defaultIcon) {
      return false;
    }
    if(e.target != popover && !popover.contains(e.target)){
      wrapper.className = wrapper.className.replace(/ __open/g,'');
    }
  };

  init();

  document.addEventListener('mouseover', function(e) {
    if (e.target == popover || popover.contains(e.target)) {
      return;
    }

    if (!isHover) {
      return;
    }
    setInformation(twemoji.parse('ğŸ‘‹'));
  })
};


